function addArticle(input){
  let regexVowel = /^a|^e|^i|^o|^u/g
  let regexU = /^u[kst]+|^uni/g
  if (regexU.test(input)){
    return "a " + input
  }
  if(regexVowel.test(input)){
    return "an " + input
  }
  return "a " + input
  
}

function capitalize(input){
  return input[0].toUpperCase() + input.slice(1)
}

function basicPluralize(input){
  let regexIES = /[^aeiou]+y$/g
  let regexES = /s$|sh$|ch$|x$|z$/g
  if (regexIES.test(input)){
    return input.slice(0,input.length-1)+"ies"
  }else if (regexES.test(input)){
    return input+"es"
  }else{
    return input+"s"
  }
  
}

